var curry = require('curry');

function debounce(wait, func) {
  var timeout;

  return function() {
    var self = this;
    var args = arguments;

    clearTimeout(timeout);
    timeout = setTimeout(function() {
      timeout = null;
      func.apply(self, args);
    }, wait);
  }; 
};

module.exports = curry(debounce);
