// comparator is a function that takes the following parameters:
//    accumulated value, current value
// and returns true or false. If it returns true, the item is concatted
// in to the return value. Otherwise, it's omitted

var concat = require('../concat');

function unique(comparator, list) {
  return list.reduce(function (acc, curr, i) {
    if (comparator(acc, curr)) {
      return concat([acc, [curr]]);
    }

    return acc;
  }, []);
}

module.exports = unique;