var ModelView = require('../model-view').extend({
  attributes: {},
  className: null
});
var CollectionView = require('../collection-view');
var getView = require('../../get-view');

var ToolbarView = CollectionView.extend({
  ModelViewClass: ModelView,
  tagName: 'ul',
  className: 'toolbar-view',
  selectable: false,
  checkable: false,
  attributes: {
    'role': 'toolbar'
  },
  switchTab: function (index) { },
  setSelected: function (index) { },
  _createView: function (name, model, index) {
    return {
      name: name,
      view: new this.ModelViewClass({
        tabindex: index === 0 ? 0 : -1,
        parent: this,
        app: this._app,
        router: this._router,
        checkable: false,
        selectable: false,
        views: [ model.get('view') ]
      })
    };
  }
});

module.exports = ToolbarView;
